(FormNr,
PositionNr,
Anzahl_Value,
Wert_Value)
Select 
FormNr,
PositionNr,
Anzahl_Value,
Wert_Value 
from ZVSTAT_Reporting
where ZVSTA_Schema=@ZVSTA_SCHEMA and ReportingPeriod=@ZVSTA_REPORTING_PERIOD
and PositionNr=@ZVSTA_POSITION
and Landkontext in (@PAYMENT_FLOW)
and LandCode not in (@ZVSTA_COUNTRY_CODE)

