(RegisterDate
,[MessageType]
,[InOutMessage]
,[TransactionType]
,[TransactionID]
,[DebitorsCountry]
,[CreditorsCountry]
,[TransactionAmountEUR]
,[ZVSTA_Position]
,[ZVSTA_Schema]
,[ZVSTA_Schema_Period]
,[ZVSTA_Reporting_Period_From]
,[ZVSTA_Reporting_Period_Till])
SELECT 
RegisterDate
,[MessageType]
,[InOutMessage]
,[TransactionType]
,[TransactionID]
,[DebitorsCountry]
,[CreditorsCountry]
,[TransactionAmountEUR]
,@ZVSTA_POSITION+''.''+ @PAYMENT_FLOW+''.''+@ZVSTA_COUNTRY_CODE
,@ZVSTA_SCHEMA
,@ZVSTA_SCHEMA_PERIOD
,@ZVSTA_REPORTDATE_FROM
,@ZVSTA_REPORTDATE_TILL
from  ZVSTA_Payments
where [ZVSTA_Schema]=@ZVSTA_SCHEMA and  [ZVSTA_Reporting_Period]=@ZVSTA_REPORTING_PERIOD
